import React, { useState, useEffect } from 'react';
import OrderList from './components/OrderList';
import AddOrderForm from './components/AddOrderForm';
import FilterButtons from './components/FilterButtons';
import { api } from './utils/api';

// Utility Functions
const formatDate = (dateString) => {
  if (!dateString) return 'N/A';
  const date = new Date(dateString);
  return date.toLocaleDateString('en-US', {
    year: 'numeric',
    month: 'short',
    day: 'numeric',
  });
};

const formatCurrency = (amount) => {
  if (amount === null || amount === undefined || isNaN(amount)) return '$0.00';
  return new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD',
  }).format(amount);
};

// Toast Component
const Toast = ({ show, message, type, onClose }) => {
  useEffect(() => {
    if (show) {
      const timer = setTimeout(onClose, 3000);
      return () => clearTimeout(timer);
    }
  }, [show, onClose]);

  if (!show) return null;

  return <div className={`toast ${type}`}>{message}</div>;
};

// Loading Spinner Component
const LoadingSpinner = ({ show }) => {
  if (!show) return null;

  return (
    <div className='loading-spinner'>
      <div className='spinner'></div>
      <p>Loading...</p>
    </div>
  );
};

// Main App Component
const App = () => {
  // State Management
  const [activeTab, setActiveTab] = useState('orders');
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(false);
  const [showOrderModal, setShowOrderModal] = useState(false);
  const [editingOrder, setEditingOrder] = useState(null);
  const [filter, setFilter] = useState('all');
  const [toast, setToast] = useState({
    show: false,
    message: '',
    type: 'success',
  });

  // Computed Values
  const stats = {
    total_orders: orders.length,
    total_revenue: orders.reduce(
      (sum, order) => sum + (order.total_amount || 0),
      0
    ),
    unique_customers: new Set(orders.map((o) => o.customer_id)).size,
  };

  const filteredOrders = orders.filter((order) => {
    if (filter === 'all') return true;
    if (filter === 'recent') {
      const orderDate = new Date(order.order_date);
      const thirtyDaysAgo = new Date();
      thirtyDaysAgo.setDate(thirtyDaysAgo.getDate() - 30);
      return orderDate >= thirtyDaysAgo;
    }
    if (filter === 'high-value') return order.total_amount >= 500;
    return true;
  });

  // Toast Management
  const showToast = (message, type = 'success') => {
    setToast({ show: true, message, type });
  };

  const hideToast = () => {
    setToast((prev) => ({ ...prev, show: false }));
  };

  // Data Loading
  const loadOrders = async () => {
    setLoading(true);
    try {
      const ordersData = await api.getOrders();
      setOrders(
        ordersData.map((order) => ({
          id: order.id,
          customer_id: order.customer_id,
          order_date: order.order_date,
          total_amount: order.total_amount || 0,
        }))
      );
    } catch (error) {
      console.error('Failed to load orders:', error);
      showToast(
        'Failed to load orders. Check if backend is running on localhost:8000',
        'error'
      );

      // Load dummy data for demo
      setOrders([
        {
          id: 1,
          customer_id: 1,
          order_date: '2025-08-30',
          total_amount: 899.99,
        },
        {
          id: 2,
          customer_id: 2,
          order_date: '2025-08-30',
          total_amount: 1799.98,
        },
      ]);
    } finally {
      setLoading(false);
    }
  };

  // Order Operations
  const handleCreateNew = () => {
    setEditingOrder(null);
    setShowOrderModal(true);
  };

  const handleEdit = (orderId) => {
    setEditingOrder(orderId);
    setShowOrderModal(true);
  };

  const handleDelete = async (orderId) => {
    setLoading(true);
    try {
      await api.deleteOrder(orderId);
      showToast('Order deleted successfully!');
      await loadOrders();
    } catch (error) {
      console.error('Failed to delete order:', error);
      showToast('Failed to delete order', 'error');
    } finally {
      setLoading(false);
    }
  };

  const handleSaveOrder = async (orderData, items, isEdit) => {
    setLoading(true);
    try {
      let result;

      if (isEdit) {
        try {
          result = await api.updateOrderWithItems(isEdit, orderData, items);
          showToast(
            `Order #${isEdit} updated successfully with ${items.length} items!`
          );
        } catch (error) {
          showToast(
            'Update endpoint not available. Please implement PUT /orders/{id}/with-items on your backend.',
            'error'
          );
          return;
        }
      } else {
        result = await api.createOrderWithItems(orderData, items);
        showToast(
          `Order #${result.order_id} created successfully with ${
            items.length
          } items! Total: ${formatCurrency(result.total_amount)}`
        );
      }

      setShowOrderModal(false);
      setEditingOrder(null);
      await loadOrders();
    } catch (error) {
      console.error('Failed to save order:', error);
      showToast(
        'Failed to save order. Please check if your backend is running on localhost:8000',
        'error'
      );
    } finally {
      setLoading(false);
    }
  };

  const handleCloseModal = () => {
    setShowOrderModal(false);
    setEditingOrder(null);
  };

  // Tab Management
  const handleTabChange = (tabName) => {
    setActiveTab(tabName);
    if (tabName === 'orders') {
      loadOrders();
    }
  };

  const refreshAnalytics = () => {
    showToast('Analytics refreshed');
  };

  // Initialize
  useEffect(() => {
    const initApp = async () => {
      console.log('React Orders Management Application initialized');

      // Test backend connection
      try {
        const response = await fetch(`${api.API_BASE_URL}/`);
        if (response.ok) {
          console.log('API connection successful');
          await loadOrders();
        } else {
          showToast('Cannot connect to backend server', 'error');
        }
      } catch (error) {
        console.error('API connection failed:', error);
        showToast(
          'Cannot connect to backend. Make sure FastAPI server is running on localhost:8000',
          'error'
        );

        // Load dummy data for demo
        setOrders([
          {
            id: 1,
            customer_id: 1,
            order_date: '2025-08-30',
            total_amount: 899.99,
          },
          {
            id: 2,
            customer_id: 2,
            order_date: '2025-08-30',
            total_amount: 1799.98,
          },
        ]);
      }
    };

    initApp();
  }, []);

  return (
    <div className='container'>
      <header>
        <h1>
          <i className='fas fa-shopping-cart'></i> React Orders Management
          System
        </h1>
        <div className='stats-bar'>
          <div className='stat-item'>
            <span className='stat-label'>Total Orders:</span>
            <span className='stat-value'>{stats.total_orders}</span>
          </div>
          <div className='stat-item'>
            <span className='stat-label'>Total Revenue:</span>
            <span className='stat-value'>
              {formatCurrency(stats.total_revenue)}
            </span>
          </div>
          <div className='stat-item'>
            <span className='stat-label'>Customers:</span>
            <span className='stat-value'>{stats.unique_customers}</span>
          </div>
        </div>
      </header>

      <nav className='tab-nav'>
        <button
          className={`tab-button ${activeTab === 'orders' ? 'active' : ''}`}
          onClick={() => handleTabChange('orders')}
        >
          Orders
        </button>
        <button
          className={`tab-button ${activeTab === 'analytics' ? 'active' : ''}`}
          onClick={() => handleTabChange('analytics')}
        >
          Analytics
        </button>
      </nav>

      {activeTab === 'orders' && (
        <div>
          <FilterButtons activeFilter={filter} onFilterChange={setFilter} />
          <OrderList
            orders={filteredOrders}
            onEdit={handleEdit}
            onDelete={handleDelete}
            onCreateNew={handleCreateNew}
            formatDate={formatDate}
            formatCurrency={formatCurrency}
          />
        </div>
      )}

      {activeTab === 'analytics' && (
        <div className='tab-content'>
          <div className='section-header'>
            <h2>
              <i className='fas fa-chart-bar'></i> Analytics Dashboard
            </h2>
            <button className='btn btn-secondary' onClick={refreshAnalytics}>
              <i className='fas fa-sync-alt'></i> Refresh
            </button>
          </div>
          <div className='analytics-grid'>
            <div className='analytics-card'>
              <h3>
                <i className='fas fa-shopping-bag'></i> Order Statistics
              </h3>
              <div>Total Orders: {stats.total_orders}</div>
              <div>Total Revenue: {formatCurrency(stats.total_revenue)}</div>
              <div>Unique Customers: {stats.unique_customers}</div>
              <div>
                Average Order Value:{' '}
                {formatCurrency(
                  stats.total_orders > 0
                    ? stats.total_revenue / stats.total_orders
                    : 0
                )}
              </div>
            </div>
          </div>
        </div>
      )}

      <AddOrderForm
        show={showOrderModal}
        editingOrder={editingOrder}
        onClose={handleCloseModal}
        onSave={handleSaveOrder}
        showToast={showToast}
        formatCurrency={formatCurrency}
      />

      <Toast
        show={toast.show}
        message={toast.message}
        type={toast.type}
        onClose={hideToast}
      />

      <LoadingSpinner show={loading} />
    </div>
  );
};

export default App;
